INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT 'c8b76da43936a2eec3d876a64a14eab551850df4', 'PLANET CHICKEN', '', 'Av. Alfredo Mendiola 3698 - C.C. Megaplaza Norte', 'Horario de atención: de lunes a jueves de 12 m. a 11 p.m.', 'Planet Chicken es la primera cadena peruana de restaurantes en ofrecerte el mejor Pollo a la brasa, acompañado de nuestra deliciosa comida criolla. Con un ambiente único que rinde tributo a la peruanidad, sus entretenidos restaurantes han sido diseñados para presentarte los mejores shows en vivo y para que puedas cantar con tus amigos en su espectacular Karaoke.', 'http://es-la.facebook.com/planetchicken', -11.99395228992712, -77.06148148435591, '02-04-2015 18:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = 'c8b76da43936a2eec3d876a64a14eab551850df4');
