INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT 'd582beb2b9ddd56933faaa66ee732ae0439e7237', 'LA CHOZA NÁUTICA DE LINCE', '', 'Jr. Brigadier Pumacahua 2374 (altura de la cdra. 9 de Av. Canevaro), Lince', 'De lunes a jueves de 1:30 p.m. a 4:30 p.m.', 'La Choza Náutica ofrece a sus comensales una gran variedad de  delicias para su paladar, con mas de 180 variedades de platos con especialidad en pescados, mariscos y parrillas de todas las carnes, acompañados de exóticos tragos; garantizándote calidad y frescura en los productos de su carta.', 'http://www.chozanautica.com/', -1, -1, '02-04-2015 18:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = 'd582beb2b9ddd56933faaa66ee732ae0439e7237');
