INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '033939d81d9daad11ba695908e063d099231e988', 'EL PAPAPA', '', 'Av. Villarán 824, Surquillo (A 4 cuadras de la Av. Aviación)', 'De lunes a jueves de 4 p.m. a 12 a.m. / viernes y sábados de 4 p.m. a 1 a.m. / domingos de 4 p.m. a 12 a.m.', 'En El Papapa tienen la consigna de hacerte sentir “como en casa” con la estrella de su carta a la cabeza: sus salchipapas ofrecidas en distintas versiones elaboradas con frescos ingredientes y una gran variedad de salsas. Por ejemplo, La Tía de Huacho, La Prima; y El Papapa. Además, todos sus platos vienen acompañados de gaseosa ilimitada.^Y para los que prefieren cuidar la línea, también hay platos ligeros pero sabrosos como La Vecina y La Hermana. ¡Te invitamos a probar su deliciosa sazón!', 'http://www.facebook.com/elpapapa01/timeline', -12.12092838578219, -77.00302235221864, '10-04-2015 20:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '033939d81d9daad11ba695908e063d099231e988');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT 'a5d1e5d223e7dcc0bc60f6a39a4a82ecdb0ba6f7', 'HIKARI POLLERÍA - RESTAURANTE LOS OLIVOS', '', 'Av. Proceres 7787 Urb. Pro, Los Olivos', 'Horario de atención: de lunes a viernes de 12 m. a 11 p.m.', 'Hikari es una palabra de origen japonés que significa luz, destello. Hace 35 años, estas connotaciones sirvieron de inspiración a una joven pareja para abrir el primer local de la cadena de restaurantes que hoy lleva ese nombre. El amor entre ellos y su pasión por hacer las cosas bien, hicieron que esta polleria, ubicada en el Rímac, tuviera pronto éxito. Hoy, Hikari cuenta con 6 locales, todos con el firme compromiso de seguir siendo tu lugar favorito para compartir momentos de placer. ¡Visítalos!', '', -11.937727148640592, -77.07457873880617, '10-04-2015 20:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = 'a5d1e5d223e7dcc0bc60f6a39a4a82ecdb0ba6f7');
