INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '2094e9ae965b8d803108f80b53446b13456f1016', 'RESTAURANTE D´PASO', '', 'Alameda Mamacona Lote 1 (Alt. Km 28 Antigua Panamericana Sur- Terminado el Parque del Recuerdo), Pachacámac', 'De martes a viernes desde las 11 a.m. / Inicio del show: 12:30 p.m.', 'Restaurante D´Paso ubicado en Mamacona ofrece una propuesta distinta a los comensales, pues los recibe con deliciosos platos típicos y con una puesta en escena que representa parte de la historia de nuestro país. Su exhibición "Luces y Sonidos del Caballo del Perú", danzas y música de la costa, sierra y selva, se convierten en todo un espectáculo.', 'http://www.facebook.com/CaballoPeruanodePaso', 37.2079057, -4.657305500000007, '10-04-2015 20:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '2094e9ae965b8d803108f80b53446b13456f1016');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT 'c8b76da43936a2eec3d876a64a14eab551850df4', 'PLANET CHICKEN', '', 'Av. Alfredo Mendiola 3698 - C.C. Megaplaza Norte', 'Horario de atención: de lunes a sábado de 12 m. a 12 p.m. /  domingo de 12 m. a 11 p.m.^^Podrás validar tu oferta solo de lunes a jueves de 12 p.m. a 11 p.m.', 'Planet Chicken es la primera cadena peruana de restaurantes en ofrecerte el mejor Pollo a la brasa, acompañado de nuestra deliciosa comida criolla. Con un ambiente único que rinde tributo a la peruanidad, sus entretenidos restaurantes han sido diseñados para presentarte los mejores shows en vivo y para que puedas cantar con tus amigos en su espectacular Karaoke.', 'http://es-la.facebook.com/planetchicken', -11.99395228992712, -77.06148148435591, '10-04-2015 20:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = 'c8b76da43936a2eec3d876a64a14eab551850df4');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '8a02ebefecc21ff8bc23e35d2755271828cf9348', 'EL RANCHO GRANDE', '', 'Mz. C, Lt. 39, Asociación Huertos de Tungasuca, Comas', 'De lunes a viernes de 12 m. a 5 p.m.', 'El Rancho Grande deleita a sus clientes desde hace 30 años brindándole una sazón especial a todos sus platillos para complacer a los amantes de la comida criolla. Pone por ejemplo, a gusto del cliente, sus espectaculares cuy chactado acompañado de papas coloradas, cecinas ahumadas de carne de caza servida con mote cusqueño, tallarines, caldos, pichones de castilla; y mucho más.', 'http://www.elranchograndedelperu.com/', -11.90975265763975, -77.0558671799011, '10-04-2015 20:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '8a02ebefecc21ff8bc23e35d2755271828cf9348');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT 'a9075c7fc5fa8496137a012348379f1fc12e4c8a', 'DON ROSALINO', '', 'Miraflores: Av. República de Panamá 5897 / San Borja: Av. Aviación 2667', 'De lunes a jueves de 12 m. a 3 p.m. y de 7 p.m. a 10 p.m. (Miraflores) / de lunes a jueves de 12:30 m. a 10 p.m. (San Borja)', 'Desde 1977, Don Rosalino mantiene la esencia de sus recetas italianas ahora complementadas con ingredientes y variantes de la gastronomía nacional. Esto hace a Don Rosalino único en su categoría, brindando la mejor experiencia de servicio en cada atención, y utilizando insumos de alta calidad para garantizar un producto único que se mantenga en la memoria y paladar de sus comensales.', 'http://www.donrosalino.com/', -12.121741332718637, -77.01793543434144, '10-04-2015 20:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = 'a9075c7fc5fa8496137a012348379f1fc12e4c8a');
