INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '2e5cff6cb008f009eba3f50e9e949f0483586563', 'ASAKUSA SUSHIBAR RESTAURANTE', 'http://cde.2.ofertop.pe/est/440.jpg?201502271800', 'Av. Javier Prado Este 2994, San Borja', 'Horario de almuerzo: de lunes a jueves de 12:30 m. a 3:30 p.m.^^Horario de cena: de lunes a jueves de 7 p.m. a 11 p.m.', 'Asakusa Sushibar Restaurante fue creado por cuatro jóvenes peruanos japoneses, con la idea de ofrecer la mejor comida japonesa fusión de Lima. Cuenta con una variada y deliciosa carta, y un espectacular y bien surtido sushibar, además de un local con un bello local de estilo japonés contemporáneo y una atención de primera.', 'http://www.asakusa.pe/', -12.086826, -76.99668099999997, '11-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '2e5cff6cb008f009eba3f50e9e949f0483586563');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '6317ba5dcc846a6464cc6e1d675043fc9d7db047', 'LA CANDELARIA', 'http://cde.2.ofertop.pe/est/408.jpg?201502271800', 'Av. Bolognesi 292, Barranco', 'Horario del espectáculo: de 10:30 p.m. a 2:30 a.m.', 'La Candelaria basa su indudable éxito en su singular propuesta dancística que lleva un mensaje rotundo de peruanidad. Sus tres pilares fundamentales para mantener la calidad de sus espectáculos son sin duda las extraordinarias particularidades de sus danzas excelentemente conducidas; su exquisita cocina, su creativo y surtido bar y la gran calidad de sus servicios.^Un hito importante de esta casa barranquina ha sido también, la introducción en sus celebraciones por Año Nuevo de un novedoso concepto artístico sobre el ancestral rito andino del “pago a la pachamama”, que se ha convertido en la mayor atracción de esta fecha. Además, encontrarás un servicio de primera, sonido inmejorable, pródiga iluminación con buena pista de baile y sobre todo un recuerdo inolvidable.', 'http://www.lacandelariaperu.com', -12.14886848053975, -77.01996508227836, '11-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '6317ba5dcc846a6464cc6e1d675043fc9d7db047');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '9ac42077ba8a693ed0796235fd22697bdf2e24ba', 'SANGUCHERÍA EL CHINITO', '', 'Grimaldo del Solar 113 (alt. 7 de Benavides), Miraflores', 'De lunes a jueves de 8 a.m. a 3 p.m. y de 6 p.m. a 10 p.m. / viernes de 8 a.m. a 10 p.m.', 'Desde 1960 ''''El Chinito'''' es conocido como la mejor y más típica Sanguchería que ofrece los más deliciosos y tradicionales sánguches peruanos. El pan con chicharrón, con lechón o con pavita, son solo algunas de las más deliciosas especialidades que podrás disfrutar junto a familia o amigos en un grato y tradicional ambiente.', 'http://www.elchinito.com.pe', -12.122257988826057, -77.02579800244678, '11-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '9ac42077ba8a693ed0796235fd22697bdf2e24ba');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '3ea2daa56cbbcf31070453525920e3279e3699d1', 'EL BOLIVARIANO', '', 'Calle Rosa Toledo 289, Pueblo Libre', 'De lunes a sábado de 12 m. a 10 p.m.', 'Bolivariano Restaurant-Bar fue una finca construida en 1780 que sobrevivió para dar vida a este tradicional y acogedor lugar. Podrás disfrutar de tus bebidas nacionales favoritas y del sabor verdadero de la Antigua Cocina Peruana.', 'http://es-es.facebook.com/elbolivarianoperu', -12.0766814, -77.06335360000003, '11-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '3ea2daa56cbbcf31070453525920e3279e3699d1');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '7425e3f3a951c8eed0a2a78e695b104e2d454eb4', 'LA PASTANA ', '', 'DELIVERY', 'Horario del delivery:  viernes de 5 p.m. a 11:00 p.m / sábados de 11 a.m a 11 p.m / domingos de 11 a.m a 9 p.m.', 'Con la recomendación del mismísimo chef Gastón Acurio, los fundadores de La Pastana supieron que se ganarían el corazón del público limeño conquistando las tierras de su estómago. Aliados con las pastas y los sentimientos que condimentan un buen plato, se dedican todos los días a crear preparaciones repletas de sabor y emociones, que dejan en las puertas de todos aquellos que deseen evocar la comida casera.', 'http://www.facebook.com/lapastana', -1, -1, '11-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '7425e3f3a951c8eed0a2a78e695b104e2d454eb4');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '0fe3d40725cd4af30fa563370641d87e4f7fc71e', 'MICALO CEVICHES Y TIRADITOS BAR', '', 'Av. Ariosto Matellini 641 Urb. Matellini, Chorrillos', 'De martes a jueves de 12 m. a 5 p.m.', 'MICALO desde el 2013 ofrece de la comida marina peruana lo mas sencillo de su repertorio: Ceviches , Tiraditos , Leche de Tigre GIGANTES , Causas y Chicharrones. Todos estos platos marinos son su ESPECIALIDAD. ¡Visítalos ya!', 'http://Micalo Ceviches y Tiraditos Bar (facebook)', -12.173586488120986, -77.00489453411103, '11-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '0fe3d40725cd4af30fa563370641d87e4f7fc71e');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '329f57fff69d289a4f55e97f6231119ab841b770', 'EDO SUSHI BAR.', 'http://cde.2.ofertop.pe/est/1163.jpg?201502271800', 'C.C. Jockey Plaza (Av. Javier Prado Este 4200, Surco)', 'De lunes a jueves de 12 m. a 10 p.m.', 'Disfruta la aventura culinaria de la comida nikkei en EDO Sushi Bar. La fusión de la cocina japonesa y peruana en una versión moderna y fresca, donde los Itamaes expertos aportarán sus conocimientos para deslumbrar a tu paladar.', 'http://www.edosushibar.com', -12.084798, -76.97837679999998, '11-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '329f57fff69d289a4f55e97f6231119ab841b770');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT 'f9afbb4b7a97e88291402f17899b8c87702b96fa', 'RESTAURANTE AJOS Y CEBOLLAS', '', 'Jr. Los Brillantes 298, Urb. Balconcillo, La Victoria', 'De martes a domingo de 12 m. a 5 p.m.', 'Restaurante Ajos y Cebollas tiene como misión llenar todas las expectativas de su público en cuanto al arte culinario nacional que profesan y su servicio personalizado. ¡No dejes de visitarlos!', 'http://es-es.facebook.com/ajosycebollasrestauranteperuanol', -12.0790619, -77.02676389999999, '11-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = 'f9afbb4b7a97e88291402f17899b8c87702b96fa');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '4ed4594dcb8881b9979ecf0b7478c45569195aeb', 'TRIBUNA MARINA', '', 'Jr. Abtao 881 (frente a la zona norte del Estadio Alejandro Villanueva), La Victoria', 'Horario de atención: de lunes a viernes 9 a.m. a 4 p.m.', 'Tribuna Marina alienta con todo, tus apetitos por la comida marina, ofreciendo los mejores potajes nacionales a base de pescados y mariscos. ¡Disfruta del mejor sabor al mejor precio!', 'http://alturl.com/vnvt5', -12.066076613188926, -77.02226638465578, '11-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '4ed4594dcb8881b9979ecf0b7478c45569195aeb');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '1626bf88bfe43134a387b27997751a5f3cbc90bd', 'BOTTICELLI TRATTORIA - PIZZERIA ', '', 'Av. Aviación 2486 B, San Borja', 'De lunes a domingo de 12 m. a 12 a.m.', 'Trattoria Pizzeria Botticelli es un restaurante italiano con especialidad en pizzas, lasagnas, risottos y pastas.', 'http://www.facebook.com/pages/Botticelli-Trattoria-Pizzeria/795620623817042?ref=hl', -12.048109781807723, -77.06217042541505, '11-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '1626bf88bfe43134a387b27997751a5f3cbc90bd');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '78d85718625a999e84429af4d1d14f9d64c9fd33', 'PASO OBLIGAO', 'http://cde.2.ofertop.pe/est/413.jpg?201502271800', 'Av. Manuel Valle Mz G Lote 10 (A 2 Kms del ingreso a Pachacámac), Pachacámac.', 'Sábado y domingo de 9 a.m. a 12 p.m.', 'Paso Obligao tiene casi 20 años en el mercado, siendo los pioneros en servir pollos a la leña en Lima. Hasta hoy tienen 4 locales, el último se encuentra en Pachacamac, que a diferencia de los otros, aquí se cocina todo en hornos de barro a la leña. Son ganadores de tres premios en el concurso anual de cocina Ecogurmet de Pachacamac. Su comida se caracteriza por ser lo más natural posible, usando productos del valle, combinados con insumos de la sierra, como las papas nativas, ocas y mashuas. La atención es de gran calidad y personalizada, y van de la mano de sus colaboradores, quienes atienden gustosamente a cada uno de sus comensales, haciéndolos sentir como en su propia casa.', 'http://pasoobligao.com/bienvenida.php', -12.22569859296973, -76.86139241914066, '11-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '78d85718625a999e84429af4d1d14f9d64c9fd33');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '80045c9d3b9a5cf0a3e541eb034c120c1ec3c3e9', 'EL SALTO DEL FRAILE', 'http://cde.2.ofertop.pe/est/1017.jpg?201502271800', 'Paseo Billinghurst (camino a la playa La Herradura), Chorrillos', 'Horario de atención: de 1 p.m. a 4 p.m. (sin música en vivo) y a partir de las 8 p.m. (con música en vivo)', 'El Salto del Fraile es un restaurante que brinda la mejor gastronomía y que está ubicado en Chorrillos, en un lugar mágico y tradicional  frente al mar con una vista alucinante, romántica e incomparable del mar de Chorrillos, donde especialistas del arte culinario elaboran platos exquisitos de comida peruana e internacional.', 'http://www.elsaltodelfraile.com/', -12.17020730698683, -77.03588157338561, '11-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '80045c9d3b9a5cf0a3e541eb034c120c1ec3c3e9');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT 'e218164b25583adf54180192c5b291c9d1f47be4', 'CLUB SANDWICH ', '', 'Av. Flora Tristán 690, Urb. Santa Patricia, La Molina', 'De lunes a domingo de 11 a.m. a 11 p.m.', 'En Club Sandwich encontrarás los más variados sánguches  de hamburguesa, pollo, chorizo, lomito, pavo, lechón, entre otros, así como ricos combos acompañados de refrescantes jugos. ¡Visítalos en sus locales de San Luis y La Molina!', 'http://www.clubsandwich.com.pe/', -12.076440142671789, -76.99573386770624, '11-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = 'e218164b25583adf54180192c5b291c9d1f47be4');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '033939d81d9daad11ba695908e063d099231e988', 'EL PAPAPA', '', 'Av. Villaran 824, Suquillo. A 4 cuadras de la Av. Aviación.', 'Horario de atención: de lunes a jueves de 4 p.m. a 12 a.m. / viernes y sábados de 4 p.m. a 1 a.m. / domingos de 4 p.m. a 12 a.m.', 'En El Papapa tienen la consigna de hacerte sentir “como en casa” con la estrella de su carta a la cabeza: sus salchipapas ofrecidas en distintas versiones elaboradas con frescos ingredientes y una gran variedad de salsas. Por ejemplo, La Tía de Huacho, La Prima; y El Papapa. Además, todos sus platos vienen acompañados de gaseosa ilimitada.^Y para los que prefieren cuidar la línea, también hay platos ligeros pero sabrosos como La Vecina y La Hermana. ¡Te invitamos a probar su deliciosa sazón!', 'http://www.facebook.com/elpapapa01/timeline', -12.12092838578219, -77.00302235221864, '11-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '033939d81d9daad11ba695908e063d099231e988');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '9baa6dcf8d62e9e31064aaf0ffd9b9ac5f1235b3', 'BARRANCO BEER COMPANY', '', 'Av. Almirante Grau 308, Barranco', 'De lunes a domingo de 12 m. a 8 p.m.', 'Barranco Beer Company nacida de ideales cerveceros y costumbres peruanas, es una cervecería de concepto vanguardista, no obstante conserva las técnicas originales para la elaboración tradicional de la cerveza. Su especialidad son las cervezas tipo Craft-Artesanal, usando en su elaboración insumos de la más alta calidad, contando con el asesoramiento profesional continuo de cerveceros y microbiólogos para un buen control de calidad.', 'http://www.facebook.com/BarrancoBeerCompany', -12.1434851, -77.02193679999999, '11-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '9baa6dcf8d62e9e31064aaf0ffd9b9ac5f1235b3');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '6ec8a46ba188ec43b90a7de0e5ccd3f5691d82a8', 'CEBICHERÍA MARÍA PASTOR', '', 'Av. Cuba 1200, Jesús María', 'De lunes a viernes de 12:30 m. a 3 p.m.', 'La Cebichería María Pastor está ubicada en el corazón de Jesús María y es el punto ideal para disfrutar de lo mejor en pescados, mariscos y comida criolla. Sus especialidades son el cebiche, la leche de tigre, el seco de cabrito y el arroz con pato, entre otros. La atención es de primera y las mesas siempre están listas para recibir a sus fieles comensales.', 'http://www.cebicheriamaria.com', -12.0750252, -77.0467066, '11-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '6ec8a46ba188ec43b90a7de0e5ccd3f5691d82a8');
