INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT 'ab4ce9deae33e87f73b49c6ab4cd7b8c100bce08', 'CIPRIANA RESTAURANTE', '', 'Calle Chamaya 153, Urb. Ponce, San Miguel', 'De lunes a viernes de 12 m. a 11 p.m.', 'Restaurante Cipriana es el único establecimiento en la zona especializado en comida peruana contemporánea preparada a base de carne fresca y sazonada con insumos de primera calidad. En Restaurante Cipriana, toman recetas de antaño y las transforman en presentaciones modernas que te harán sentir como si estuvieras en casa. Cuentan con una amplia variedad de platos, para todos los gustos, ya sea niños, jóvenes y adultos, lo que los cataloga como un espacio 100% familiar.', 'http://www.cipriana.pe/', -12.075245641124841, -77.08215707116392, '19-03-2015 16:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = 'ab4ce9deae33e87f73b49c6ab4cd7b8c100bce08');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '0836bab8018e3d83f59bec83ef344ceb330befdb', 'MANDARÍN RESTAURANT-BUFFET', 'http://cde.2.ofertop.pe/est/1294.jpg?201502271800', 'Av. Javier Prado Este 1860 (cruce de Guardia Civil con Javier Prado), San Borja', 'Jueves 26 de marzo de 7:30 p.m. a 1 a.m.', 'Mandarín Restaurant-Buffet tiene como misión satisfacer tu paladar, al momento de experimentar con nuevos sabores. Esto basado en la excelencia y calidad de sus productos y servicio. ¡Opta por una aventura gastronímica distinta!', 'http://www.buffetmandarin.com', -12.088933306755369, -77.00677001026463, '19-03-2015 16:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '0836bab8018e3d83f59bec83ef344ceb330befdb');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '3ea2daa56cbbcf31070453525920e3279e3699d1', 'EL BOLIVARIANO', '', 'Calle Rosa Toledo 289, Pueblo Libre', 'De lunes a sábado de 12 m. a 10 p.m.', 'Bolivariano Restaurant-Bar fue una finca construida en 1780 que sobrevivió para dar vida a este tradicional y acogedor lugar. Podrás disfrutar de tus bebidas nacionales favoritas y del sabor verdadero de la Antigua Cocina Peruana.', 'http://es-es.facebook.com/elbolivarianoperu', -12.0766814, -77.06335360000003, '19-03-2015 16:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '3ea2daa56cbbcf31070453525920e3279e3699d1');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '479f0d4b70bb19be63f1451f5eca669c631d0e71', 'PURO PERÚ', '', 'Av. Republica de Panamá 258, Barranco', 'Horario de atención: de martes a sábado de 12 m. a 5 p.m.', 'Puro Perú es un prestigioso restaurante con una variedad de platos peruanos, donde disfrutarás de una nueva propuesta con originales variedades culinarias de nuestras regiones. Encontrarás en su buffet los más exquisitos pescados y mariscos, comida criolla, platos calientes, ensaladas, salteados y chaufas, pastas, sushi & rolls, flambeados, y exquisitos postres.', 'http://www.puroperu.com.pe/', -12.140015933102077, -77.01745524655763, '19-03-2015 16:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '479f0d4b70bb19be63f1451f5eca669c631d0e71');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT 'a41021863714231e90f4f2606a4f3fa0caa4b33f', 'HIKARI POLLERÍA - RESTAURANTE DE SAN MIGUEL', '', 'Av. La Mar 2339, San Miguel', 'Horario de atención: de lunes a viernes de 12 m. a 11 p.m.', 'Hikari es una palabra de origen japonés que significa luz, destello. Hace 35 años, estas connotaciones sirvieron de inspiración a una joven pareja para abrir el primer local de la cadena de restaurantes que hoy lleva ese nombre. El amor entre ellos y su pasión por hacer las cosas bien, hicieron que esta polleria, ubicada en el Rímac, tuviera pronto éxito. Hoy, Hikari cuenta con 6 locales, todos con el firme compromiso de seguir siendo tu lugar favorito para compartir momentos de placer. ¡Visítalos!', 'http://www.hikari.pe', -12.074854991462232, -77.08322528308105, '19-03-2015 16:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = 'a41021863714231e90f4f2606a4f3fa0caa4b33f');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '6ec8a46ba188ec43b90a7de0e5ccd3f5691d82a8', 'CEBICHERÍA MARÍA PASTOR', '', 'Av. Cuba 1200, Jesús María', 'Horario de atención: de lunes a domingo de 7 p.m. a 10 p.m.', 'La Cebichería María Pastor está ubicada en el corazón de Jesús María y es el punto ideal para disfrutar de lo mejor en pescados, mariscos y comida criolla. Sus especialidades son el cebiche, la leche de tigre, el seco de cabrito y el arroz con pato, entre otros. La atención es de primera y las mesas siempre están listas para recibir a sus fieles comensales.', 'http://www.cebicheriamaria.com', -12.0750252, -77.0467066, '19-03-2015 16:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '6ec8a46ba188ec43b90a7de0e5ccd3f5691d82a8');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '9baa6dcf8d62e9e31064aaf0ffd9b9ac5f1235b3', 'BARRANCO BEER COMPANY', '', 'Av. Almirante Grau 308, Barranco', 'De lunes a domingo de 12 m. a 8 p.m.', 'Barranco Beer Company nacida de ideales cerveceros y costumbres peruanas, es una cervecería de concepto vanguardista, no obstante conserva las técnicas originales para la elaboración tradicional de la cerveza. Su especialidad son las cervezas tipo Craft-Artesanal, usando en su elaboración insumos de la más alta calidad, contando con el asesoramiento profesional continuo de cerveceros y microbiólogos para un buen control de calidad.', 'http://www.facebook.com/BarrancoBeerCompany', -12.1472730364287, -77.02219857344971, '19-03-2015 16:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '9baa6dcf8d62e9e31064aaf0ffd9b9ac5f1235b3');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '6317ba5dcc846a6464cc6e1d675043fc9d7db047', 'LA CANDELARIA', 'http://cde.2.ofertop.pe/est/408.jpg?201502271800', 'Av. Bolognesi 292, Barranco', 'Horario del espectáculo: de 10:30 p.m. a 2:30 a.m.', 'La Candelaria basa su indudable éxito en su singular propuesta dancística que lleva un mensaje rotundo de peruanidad. Sus tres pilares fundamentales para mantener la calidad de sus espectáculos son sin duda las extraordinarias particularidades de sus danzas excelentemente conducidas; su exquisita cocina, su creativo y surtido bar y la gran calidad de sus servicios.^Un hito importante de esta casa barranquina ha sido también, la introducción en sus celebraciones por Año Nuevo de un novedoso concepto artístico sobre el ancestral rito andino del “pago a la pachamama”, que se ha convertido en la mayor atracción de esta fecha. Además, encontrarás un servicio de primera, sonido inmejorable, pródiga iluminación con buena pista de baile y sobre todo un recuerdo inolvidable.', 'http://www.lacandelariaperu.com', -12.14886848053975, -77.01996508227836, '19-03-2015 16:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '6317ba5dcc846a6464cc6e1d675043fc9d7db047');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '884b64060009dd2db01745ab23804340db9f7868', 'NOVOTEL LIMA', '', 'Av. Victor Andrés Belaunde 198, San Isidro', 'De lunes a domingos de 6 a.m. a 10 a.m.', 'Novotel es una prestigiosa cadena hotelera con casi 400 hoteles en todo el mundo. Novotel Lima se encuentra estratégicamente ubicado en el distrito de San Isidro y cuenta con 208 habitaciones, 7 salas de reuniones, wi fi en todos sus ambientes, restaurante, bar, gimnasio, piscina y estacionamiento para ofrecer un servicio de calidad a sus huéspedes. Los servicios que ofrece lo convierten en el lugar ideal para viajes de negocios y de placer.', 'http://www.novotel.com/es/hotel-6339-novotel-lima/index.shtml', -12.096011, -77.03656649999999, '19-03-2015 16:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '884b64060009dd2db01745ab23804340db9f7868');
