INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '78d85718625a999e84429af4d1d14f9d64c9fd33', 'PASO OBLIGAO', 'http://cde.2.ofertop.pe/est/413.jpg?201503191646', 'Av. Manuel Valle Mz G Lote 10 (A 2 Kms del ingreso a Pachacámac), Pachacámac.', 'Sábados de 12 m. a 4 p.m.', 'Paso Obligao tiene casi 20 años en el mercado, siendo los pioneros en servir pollos a la leña en Lima. Hasta hoy tienen 4 locales, el último se encuentra en Pachacamac, que a diferencia de los otros, aquí se cocina todo en hornos de barro a la leña. Son ganadores de tres premios en el concurso anual de cocina Ecogurmet de Pachacamac. Su comida se caracteriza por ser lo más natural posible, usando productos del valle, combinados con insumos de la sierra, como las papas nativas, ocas y mashuas. La atención es de gran calidad y personalizada, y van de la mano de sus colaboradores, quienes atienden gustosamente a cada uno de sus comensales, haciéndolos sentir como en su propia casa.', 'http://pasoobligao.com/bienvenida.php', -12.22569859296973, -76.86139241914066, '25-03-2015 00:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '78d85718625a999e84429af4d1d14f9d64c9fd33');
