INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '5a59adcc09c83c0dcb6d30fa74ead76bb5549f10', 'LA DAMA JUANA', 'http://cde.2.ofertop.pe/est/1162.jpg?201503271751', 'Av. República de Panamá 230, Barranco', 'De lunes a domingo de 12:30 m a 4 p.m. (almuerzo) / de lunes a domingo de 7:30 p.m. a 10 p.m. (cena)', 'La Dama Juana es un reconocido restaurante ubicado en el distrito de Barranco. Como buen representante de nuestra gastronomía ofrece a sus comensales una variada carta, así como los más selectos tragos y bebidas. Cuenta con elegantes y acogedores ambientes donde podrás disfrutar los mejores potajes de nuestra comida, viviendo una experiencia inolvidable.', 'http://ladamajuana.com.pe/', -12.14013131100782, -77.01785221349184, '30-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '5a59adcc09c83c0dcb6d30fa74ead76bb5549f10');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '78d85718625a999e84429af4d1d14f9d64c9fd33', 'PASO OBLIGAO', 'http://cde.2.ofertop.pe/est/413.jpg?201503271751', 'Av. Manuel Valle, Mz. G Lt. 10 (A 2 Km. del ingreso a Pachacámac), Pachacámac.', 'Horario de atención: sábados de 9 a.m. a 12 p.m.', 'Paso Obligao tiene casi 20 años en el mercado, siendo los pioneros en servir pollos a la leña en Lima. Hasta hoy tienen 4 locales, el último se encuentra en Pachacamac, que a diferencia de los otros, aquí se cocina todo en hornos de barro a la leña. Son ganadores de tres premios en el concurso anual de cocina Ecogurmet de Pachacamac. Su comida se caracteriza por ser lo más natural posible, usando productos del valle, combinados con insumos de la sierra, como las papas nativas, ocas y mashuas. La atención es de gran calidad y personalizada, y van de la mano de sus colaboradores, quienes atienden gustosamente a cada uno de sus comensales, haciéndolos sentir como en su propia casa.', 'http://pasoobligao.com/bienvenida.php', -12.22569859296973, -76.86139241914066, '30-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '78d85718625a999e84429af4d1d14f9d64c9fd33');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT '9c002b27a09c942b64cd466828e429f710e2d72a', 'CUARTO & MITAD BEEF BAR', '', 'Av. Comandante Espinar 798, Miraflores', 'Horario de reservas: de lunes a viernes de 12:30 m. a 6 p.m.', 'Cuarto & Mitad Beef Bar tiene como especialidad a las carnes a la parrilla de la más selecta calidad, importada y nacional. Cuentan con platos de comida novoandina, pastas, pescados y mariscos; y una acogedora zona de bar donde podrás disfrutar de variados tragos. Su calidad en el servicio con atención personalizada cierra con broche de oro una experiencia gastronómica inolvidable.', 'http://restaurantcuartoymitad.com/', -12.1112845, -77.036989, '30-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = '9c002b27a09c942b64cd466828e429f710e2d72a');
INSERT INTO empresa (id,nombre,logo,direccion,horario,descripcion,sitio_web,latitud,longitud, fecha_creacion) SELECT 'ab4ce9deae33e87f73b49c6ab4cd7b8c100bce08', 'CIPRIANA RESTAURANTE', '', 'Calle Chamaya 153, Urb. Ponce, San Miguel', 'Horario de atención: de lunes a viernes de 12 m. a 10 p.m.', 'Restaurante Cipriana es el único establecimiento en la zona especializado en comida peruana contemporánea preparada a base de carne fresca y sazonada con insumos de primera calidad. En Restaurante Cipriana, toman recetas de antaño y las transforman en presentaciones modernas que te harán sentir como si estuvieras en casa. Cuentan con una amplia variedad de platos, para todos los gustos, ya sea niños, jóvenes y adultos, lo que los cataloga como un espacio 100% familiar.', 'http://www.cipriana.pe/', -12.075245641124841, -77.08215707116392, '30-03-2015 12:00:00' WHERE NOT EXISTS (SELECT id FROM empresa WHERE id = 'ab4ce9deae33e87f73b49c6ab4cd7b8c100bce08');
